function Test-ParameterWasPassed([string] $OptionalParameter = $null)
{
	if ($PSBoundParameters.ContainsKey('OptionalParameter'))
	{
		Write-Output 'OptionalParameter was passed.'
	}
	else
	{
		Write-Output 'OptionalParameter was not passed.'
	}
}

# Array addition:
$items = @()
foreach ($i in 0..10) {
    $items += $i
}

# Can be easily replaced with explicit assignment:
$items = foreach ($i in 0..10) {
    $i
}

# And, when not possible, a List<T> is recommended:
$items = [System.Collections.Generic.List[int]]::new()
foreach ($i in 0..10) {
    $items.Add($i)
}
